mapper.update:
update product_image
set product_id = #{productId,jdbcType=BIGINT},
  image_url = #{imageUrl,jdbcType=VARCHAR},
  is_main = #{isMain,jdbcType=BIT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, product_id, image_url, is_main
from product_image
where id = #{id,jdbcType=BIGINT}
